import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor() {
    super();

    this.state = {
      string: 'Hello Lewis'
    }
  }

  render() {
    return (
        <div className="App">
          <header className="App-header">

            <img src={logo} className="App-logo" alt="logo" />

            <p>{this.state.string}</p>

            <button onClick={() => {

            if(this.state.string == "Hello Lewis") {
                this.setState({ string: 'Hello Emma'})
             } else this.setState({string: 'Hello Lewis'})
            }}>Hello ?</button>

          </header>
        </div>
      );
  }
}

export default App;
